"use strict";

// function for calculating the insuline units to take after a meal based on the meal and the patiënts weight
function BolusCalculation(weight, carbsOfMeal) {
  // checking if inputs are legitimate
  if (weight > 450 || weight < 0.25 || carbsOfMeal < 1) {
    console.log("Are you sure you entered the right information?");
  } else {
    //calculate the total daily needed insuline intake base on weight
    var insulineTotal = weight * 0.55; // the basal dose is 50% of the daily intake, this will be printed to the console

    var basalDose = insulineTotal / 2;
    console.log(basalDose); // calculates the amount of carbs per insuline unit

    var ratio = 500 / insulineTotal; // the insuline needed to be taken after the meal

    var insulineDose = carbsOfMeal / ratio;
    console.log(insulineDose);
    return insulineDose;
  }
} //Run the function
// BolusCalculation(weight, carbs);


